const Channel = ({ channel, audioClips, series }) => {
	return (
		<div>
			<header>Podcast</header>

			<h1>{channel.title}</h1>

			<h2>Series</h2>
			{series.map((serie, key) => {
				return (
					<div key={key}>
						<h3>{serie.title}</h3>
					</div>
				)
			})}

			<h2>Ultimos podcast</h2>
			{audioClips.map((audioClip, key) => {
				return (
					<div key={key}>
						<h3>{audioClip.title}</h3>
					</div>
				)
			})}

			<style jsx>{`
				header {
					color: #fff;
					background: #8756ca;
					padding: 15px;
					text-align: center;
				}
				.channels {
					display: grid;
					grid-gap: 15px;
					padding: 15px;
					grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
				}
				.channel {
					display: block;
					border-radius: 3px;
					box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.15);
					margin-bottom: 0.5em;
				}
				.channel img {
					width: 100%;
				}

				h1 {
					padding: 15px;
					font-weight: 600;
				}

				h2 {
					padding: 5px;
					text-align: center;
				}
			`}</style>

			<style jsx global>
				{`
					body {
						margin: 0;
						font-family: system-ui;
					}
				`}
			</style>
		</div>
	)
}

Channel.getInitialProps = async ({ query }) => {
	let idChannel = query.id
	// fetching channel data
	let reqChannel = await fetch(
		`https://api.audioboom.com/channels/${idChannel}`
	)
	let dataChannel = await reqChannel.json()
	let channel = dataChannel.body.channel

	// fetching audios
	let reqAudios = await fetch(
		`https://api.audioboom.com/channels/${idChannel}/audio_clips`
	)
	let dataAudios = await reqAudios.json()
	let audioClips = dataAudios.body.audio_clips

	//fetching childChannels
	let reqSeries = await fetch(
		`https://api.audioboom.com/channels/${idChannel}/child_channels`
	)
	let dataSeries = await reqSeries.json()
	let series = dataSeries.body.channels

	return { channel, audioClips, series }
}

export default Channel
