const PodcastList = ({ audioClips }) => {
	return (
		<>
			<h2>Ultimos podcast</h2>
			{audioClips.map((audioClip) => (
				<Link href={`/podcast?id=${audioClip.id}`} key={audioClip.id}>
					<a className="podcast">
						<h3>{audioClip.title}</h3>
						<div className="meta">
							{Math.ceil(audioClip.duration / 60)} minutes
						</div>
					</a>
				</Link>
			))}
		</>
	)
}

export default PodcastList
