const ChannelGrid = (props) => {
	const { channels } = props
	return (
		<>
			<div className="channels">
				{channels.map((channel, key) => (
					<Link href={`/channel?id=${channel.id}`} key={key}>
						<a className="channel">
							<img src={channel.urls.logo_image.original} alt="channel image" />
							<h3>{channel.title}</h3>
						</a>
					</Link>
				))}
			</div>
			<style jsx>{`
				.channels {
					display: grid;
					grid-gap: 15px;
					padding: 15px;
					grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
				}
				.channel {
					display: block;
					border-radius: 3px;
					background: #ffffff;
					box-shadow: 20px 20px 60px #d9d9d9, -20px -20px 60px #ffffff;
					margin-bottom: 0.5em;
				}
				a.channel {
					display: block;
					margin-bottom: 0.5em;
					color: #333;
					text-decoration: none;
				}
				.channel img {
					width: 100%;
					border-radius: 3px;
				}

				h3 {
					padding: 5px;
					text-align: center;
				}
			`}</style>
		</>
	)
}

export default ChannelGrid
