import Link from 'next/link'

const Layout = (props) => {
	const { children, title } = props

	return (
		<div>
			<header>
				<Link href="/">
					<a>{title}</a>
				</Link>
			</header>
			{children}

			<style jsx>{`
				header {
					color: black;
					text-transform: uppercase;
					padding: 15px;
					text-align: center;
					font-weight: 600;
					background: #ffffff;
					box-shadow: 20px 20px 60px #d9d9d9, -20px -20px 60px #ffffff;
				}

				header a {
					color: black;
					text-decoration: none;
				}
			`}</style>

			<style jsx global>
				{`
					body {
						margin: 0;
						font-family: system-ui;
						background: white;
					}
				`}
			</style>
		</div>
	)
}

export default Layout
