import Link from 'next/link'

export default class extends React.Component {
	static async getInitialProps() {
		let req = await fetch('https://api.audioboom.com/channels/recommended')
		let { body: channels } = await req.json()
		return { channels }
	}

	render() {
		const { channels } = this.props
		return (
			<div>
				<header>Podcast</header>
				<div className="channels">
					{channels.map((channel, key) => (
						<Link href={`/channel?id=${channel.id}`} key={key}>
							<a className="channel">
								<img
									src={channel.urls.logo_image.original}
									alt="channel image"
								/>
								<h2>{channel.title}</h2>
							</a>
						</Link>
					))}
				</div>

				<style jsx>{`
					header {
						color: black;
						text-transform: uppercase;
						background: #dddddd;
						padding: 15px;
						text-align: center;
					}
					.channels {
						display: grid;
						grid-gap: 15px;
						padding: 15px;
						grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
					}
					.channel {
						display: block;
						background: white;
						border-radius: 3px;
						box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.15);
						margin-bottom: 0.5em;
					}
					a.channel {
						display: block;
						margin-bottom: 0.5em;
						color: #333;
						text-decoration: none;
					}
					.channel img {
						width: 100%;
					}

					h2 {
						padding: 5px;
						text-align: center;
					}
				`}</style>

				<style jsx global>
					{`
						body {
							background: #2d2d2d;
							margin: 0;
							font-family: system-ui;
						}
					`}
				</style>
			</div>
		)
	}
}
