import Link from 'next/link'

const Podcast = ({ audioClip }) => {
	return (
		<div>
			<header>Podcast</header>
			<div className="cover">
				<img src={audioClip.urls.image} alt="" />
			</div>
			<div className="info">
				<h1>{audioClip.title}</h1>
				<p>Duration: {Math.ceil(audioClip.duration / 60)} minutes</p>

				<audio controls autoPlay={false}>
					<source src={audioClip.urls.high_mp3} type="audio/mpeg" />
				</audio>
			</div>

			<style jsx>{`
				header {
					color: #fff;
					background: #3f3e3f;
					padding: 15px;
					text-align: center;
				}

				.cover {
					width: 100%;
					text-align: center;
					background: black;
				}

				.cover img {
					width: 70%;
					margin: 40px auto;
					border-radius: 3px;
					box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.15);
				}

				.info {
					padding-top: 40px;
					text-align: center;
					background: #3f3e3f;
					color: white;
				}

				.channels {
					display: grid;
					grid-gap: 15px;
					padding: 15px;
					grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
				}
				a.channel {
					display: block;
					margin-bottom: 0.5em;
					color: #333;
					text-decoration: none;
				}
				.channel img {
					border-radius: 3px;
					box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.15);
					width: 100%;
				}
				h1 {
					margin: 0;
					font-weight: 600;
					padding: 15px;
				}
				h2 {
					padding: 5px;
					font-size: 0.9em;
					font-weight: 600;
					margin: 0;
					text-align: center;
				}

				.podcast {
					display: block;
					text-decoration: none;
					color: #333;
					padding: 15px;
					border-bottom: 1px solid rgba(0, 0, 0, 0.2);
					cursor: pointer;
				}
				.podcast:hover {
					color: #000;
				}
				.podcast h3 {
					margin: 0;
				}
				.podcast .meta {
					color: #666;
					margin-top: 0.5em;
					font-size: 0.8em;
				}
			`}</style>

			<style jsx global>{`
				body {
					margin: 0;
					font-family: system-ui;
					background: #3f3e3f;
				}
			`}</style>
		</div>
	)
}

Podcast.getInitialProps = async ({ query }) => {
	let idAudioClip = query.id

	// getting audio clip
	let reqAudioClip = await fetch(
		`https://api.audioboom.com/audio_clips/${idAudioClip}.mp3`
	)
	let dataAudioClip = await reqAudioClip.json()
	let audioClip = dataAudioClip.body.audio_clip

	return { audioClip }
}

export default Podcast
