export default class extends React.Component {
	render() {
		return (
			<div className="main">
				<h1>About</h1>
				<p>Here is the about section</p>

				<img src="/platzi-logo.png" alt="platzi logo" />

				<style jsx>{`
					h1 {
						color: white;
					}
					p {
						color: lightgray;
					}
					img {
						max-width: 50%;
						display: block;
						margin: 0 auto;
					}
					.main {
						max-width: 100%;
						text-align: center;
						font: verdana;
					}
				`}</style>

				<style jsx global>
					{`
						body {
							background: black;
						}
					`}
				</style>
			</div>
		)
	}
}
