import Link from 'next/link'

const Channel = ({ channel, audioClips, series }) => {
	return (
		<div>
			<header>Podcast</header>
			<nav>
				<Link href="/">
					<a>Volver</a>
				</Link>
			</nav>
			<div
				className="banner"
				style={{
					backgroundImage: `url(${channel.urls.banner_image.original})`
				}}
			></div>
			<h1>{channel.title}</h1>
			{series.length > 0 && (
				<div>
					<h2>Series</h2>
					<div className="channels">
						{series.map((serie) => (
							<Link href={`/channel?id=${serie.id}`} key={serie.id}>
								<a className="channel">
									<img src={serie.urls.logo_image.original} alt="" />
									<h2>{serie.title}</h2>
								</a>
							</Link>
						))}
					</div>
				</div>
			)}

			<h2>Ultimos podcast</h2>
			{audioClips.map((audioClip, key) => (
				<Link href={`/podcast?id=${audioClip.id}`} key={audioClip.id}>
					<a className="podcast">
						<h3>{audioClip.title}</h3>
						<div className="meta">
							{Math.ceil(audioClip.duration / 60)} minutes
						</div>
					</a>
				</Link>
			))}

			<style jsx>{`
				header {
					color: black;
					text-transform: uppercase;
					padding: 15px;
					text-align: center;
					font-weight: 600;
					background: #ffffff;
					box-shadow: 20px 20px 60px #d9d9d9, -20px -20px 60px #ffffff;
				}
				nav {
					position: absolute;
					display: inline-block;
					background: #ffffff;
					box-shadow: 20px 20px 60px #d9d9d9, -20px -20px 60px #ffffff;
					padding: 8px;
					margin: 10px;
				}

				nav a {
					color: white;
					text-decoration: none;
				}

				.banner {
					width: 100%;
					padding-bottom: 25%;
					background-position: 50% 50%;
					background-size: cover;
					background-color: #aaa;
					box-shadow: 20px 20px 60px #d9d9d9, -20px -20px 60px #ffffff;
				}

				.channels {
					display: grid;
					grid-gap: 15px;
					padding: 15px;
					grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
				}
				a.channel {
					display: block;
					margin-bottom: 0.5em;
					color: #333;
					text-decoration: none;
				}
				.channel img {
					border-radius: 3px;
					box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.15);
					width: 100%;
				}
				h1 {
					font-weight: 600;
					text-shadow: 0 0 11px #ffffff;
					background: rgba(255, 255, 255, 0.3);
					padding: 5px;
					text-align: center;
				}
				h2 {
					padding: 5px;
					font-size: 0.9em;
					font-weight: 600;
					margin: 20px auto 0px auto;
					text-align: center;
				}

				.podcast {
					display: block;
					text-decoration: none;
					color: #333;
					padding: 15px;
					border-bottom: 1px solid rgba(0, 0, 0, 0.2);
					cursor: pointer;
				}
				.podcast:hover {
					color: #000;
				}
				.podcast h3 {
					margin: 0;
				}
				.podcast .meta {
					color: #666;
					margin-top: 0.5em;
					font-size: 0.8em;
				}
			`}</style>

			<style jsx global>{`
				body {
					margin: 0;
					font-family: system-ui;
					background: white;
				}
			`}</style>
		</div>
	)
}

Channel.getInitialProps = async ({ query }) => {
	let idChannel = query.id

	let [reqChannel, reqSeries, reqAudios] = await Promise.all([
		fetch(`https://api.audioboom.com/channels/${idChannel}`),
		fetch(`https://api.audioboom.com/channels/${idChannel}/child_channels`),
		fetch(`https://api.audioboom.com/channels/${idChannel}/audio_clips`)
	])

	// getting channel data
	let dataChannel = await reqChannel.json()
	let channel = dataChannel.body.channel
	// getting audios data
	let dataAudios = await reqAudios.json()
	let audioClips = dataAudios.body.audio_clips
	// getting chiled channels data
	let dataSeries = await reqSeries.json()
	let series = dataSeries.body.channels

	return { channel, audioClips, series }
}

export default Channel
