export default class extends React.Component {
	render() {
		return (
			<div className="main">
				<img src="/platzi-logo.png" alt="platzi logo" />
				<h1>About</h1>
				<h2>This is the about section</h2>

				<style jsx>{`
					h1 {
						color: white;
					}
					h2 {
						color: lightgray;
					}
					img {
						max-width: 20%;
						display: block;
						margin: 30px auto;
					}
					.main {
						max-width: 100%;
						text-align: center;
					}
				`}</style>

				<style jsx global>
					{`
						body {
							background: black;
							font-family: verdana, arial;
						}
						h1 {
							font-size: 20px;
						}
						h2 {
							font-size: 16px;
						}
						p {
							font-size: 12px;
						}
					`}
				</style>
			</div>
		)
	}
}
